import React, {Component} from 'react';
import Charity from '../containers/Charity';

// CSS
import "./charityhome.css";

export default class Charityhome extends Component {
  constructor(props) {
    super(props);
    this.state = {
        charities: []
    };
  }

  componentDidMount() {
    this.setState({charities: [{
        id: 1,
        name: "Red Cross",
        body: "Helping children hospitals - 200 tokens",
        date: "Dec 5 - 25",
        item: "10 First Aid Kits - 200 Tokens",
        verified: "5 Stars"
    }, {
        id: 2,
        name: "Global Crysis Foundation",
        body: "Earthquakes Relief",
        date: "Urgent: Dec 17",
        item: "Transporation - Bonus 300 Tokens",
        verified: "5 Stars"
    }, {
        id: 3,
        name: "Local Food Bank",
        body: "HabourFront Toronto",
        date: "Dec 15 - 20",
        item: "20 Canned foods -50 Tokens",
        verified: "5 Stars"
    }
    ]});
}

renderItems() {
    return this.state.items.map(item => {
        return <DonationItem
            key={"item-" + item.id}
            name={item.name}
            body={item.body}
            price={item.price}
            donate={this.props.donate}
        />
    });
}
  render() {
    return (
        <div class="charityhome-container">
            <h1>Trusted Charities:</h1>

            <div className="charity-list">
                <h2>Red Cross</h2>
                <h2>Recent Global Relief Crisis</h2>
                <h2>Food Banks</h2>
                {/* <Charity select={this.props.select} /> */}
            </div>

            <h3>Help donate, recieve tokens and save on future inventory purshases from manufactures. Support local farmers!</h3>

            <h3>Bonus tokens for bulk orders and urgent requests!</h3>
            <h3>Sign up for a verified account for bonus incentives!</h3>


        </div>

    )
  }
}
